function GFUpdate(step = '') {
    var duration_val = (parseInt($('#year_license').val()) == 1) ? 'year' : 'years';
    duration_val = $('#year_license').val() + ' ' + duration_val;
    var visa_val = (parseInt($('#visas').val()) == 1) ? 'visa' : 'visas';
    visa_val = $('#visas').val() + ' ' + visa_val;
    var ba_val_inp = "";
    var request = $('input[name="request"]:checked').val();
    var form_ccd = '';
    var step_text = ['', '', 'duration', 'visa', 'activities', 'additional activities', 'result'];

    costCalData.activities_arr.forEach(function (val) { ba_val_inp += val + ", "; });
    costCalData.additional_activities_arr.forEach(function (val) { ba_val_inp += val + ", "; });
    ba_val_inp = ba_val_inp.slice(0, -2);

    $('.form_name input').val($('#name').val());
    $('.form_phone input').val(phone_input_ini.getNumber().split(' ').join(''));
    $('.form_email input').val($('#email').val());
    $('.form_duration input').val(duration_val);
    $('.form_visas input').val(visa_val);
    $('.form_business_activities input').val(ba_val_inp);
    $('.form_request input').val(request);

    if (step != '') {
        if (step > 1) { form_ccd += ''; }
        if (step > 2) { form_ccd += 'User needs license for ' + duration_val + '.'; }
        if (step > 3) { form_ccd += ' User needs ' + visa_val + '.'; }
        if (step > 4) { form_ccd += ' User needs license for ' + ba_val_inp + '.'; }
        form_ccd += ' User quit at ' + step_text[step] + ' step.';
        $('.form_ccd input').val(form_ccd);
        $('#gform_30').submit();
    } else {
        form_ccd = 'User needs license for ' + duration_val + '. User needs ' + visa_val + '. User needs license for ' + ba_val_inp + '.';
        if (request == "online payment") {
            $('.form_payment_received input').val('Yes');
            form_ccd += ' User paid online';
        } else {
            $('.form_payment_received input').val('No');
            form_ccd += ' User needs a ' + request;
        }
        $('.next-button span').text('Please wait...');
        $('.form_ccd input').val(form_ccd);
        halfForm = false;
        $('#gform_4').submit();
    }
}

function sessionValues() {
    $('.loading-outer').removeClass('d-none');
    var session_vals = {};
    session_vals.name = $('#name').val();
    session_vals.email = $('#email').val();
    session_vals.phone = $('#phone').val();
    session_vals.country_code = phone_input_ini.getSelectedCountryData().iso2;
    session_vals.year_license = $('#year_license').val();
    session_vals.visas = $('#visas').val();
    session_vals.activities_arr = costCalData.activities_arr;
    session_vals.additional_activities_arr = costCalData.additional_activities_arr;
    session_vals.source = $('#source').val();
    session_vals.source_url = $('#source_url').val();
    var session_vals_str = JSON.stringify(session_vals);

    $.ajax({
        type: 'post',
        url: 'https://www.meydanfz.ae/wp-admin/admin-ajax.php',
        data: {
            session_value: session_vals_str,
            action: 'cost_cal_session'
        },
        success: (res) => {
            var res_json = JSON.parse(res);
            // console.log(res_json);
            if (res_json.order_data.order_paypage_url != undefined) {
                $('.loading-outer').addClass('d-none');
                $('.pay-now-button').removeClass('d-none');
                $('.next-button').addClass('d-none');
                $('.pay-now-button').attr('href', res_json.order_data.order_paypage_url);
            } else {
                $('.loading-outer').addClass('d-none');
                $('.pay-now-button').addClass('d-none');
                $('.next-button').removeClass('d-none');
                $('.form-check-input[value="call back"]').prop('checked', true);
                calcTotal();
                Swal.fire("Sorry", "The online payment system seems to be having a problem, please try again later.", "warning");
            }
        }
    });
}


