function updateSteps(nextStep) {
    $('.steps-ul li a').removeClass('done');
    for (var i = nextStep + 1; i > 0; i--) {
        $('.steps-ul li:nth-child(' + i + ') a').addClass('done');
    }
    if (window.innerHeight < 900) {
        $('html, body').animate({ scrollTop: $('#main_title').offset().top }, 300);
    }
}

function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

function autocomplete(inp, arr) {
    var currentFocus;
    inp.addEventListener("input", function (e) {
        var a, b, i, val = this.value;
        var strIndex = 0;
        var tempSubString = '';
        var tempString = '';
        closeAllLists();
        if (!val) { return false; }
        currentFocus = -1;

        a = document.createElement("DIV");
        a.setAttribute("id", this.id + "autocomplete-list");
        a.setAttribute("class", "autocomplete-items");
        this.parentNode.appendChild(a);
        for (i = 0; i < arr.length; i++) {
            if ((arr[i].toUpperCase()).includes(val.toUpperCase())) {
                tempString = arr[i];
                strIndex = (arr[i].toUpperCase()).indexOf(val.toUpperCase());
                tempSubString = "<strong>" + arr[i].substr(strIndex, val.length) + "</strong>";
                tempString = tempString.replace(val, tempSubString);
                b = document.createElement("DIV");
                b.innerHTML = tempString;
                b.innerHTML += "<input type='hidden' value='" + arr[i] + "'>";
                b.addEventListener("click", function (e) {
                    let selectedVal = (this.getElementsByTagName("input")[0].value).trim();
                    if (!(costCalData.additional_activities_arr.indexOf(selectedVal) > -1)) {
                        costCalData.additional_activities_arr.push(this.getElementsByTagName("input")[0].value);
                        $('.additional-activities').append('<li>' + selectedVal + '<span class="remove-aa" onClick="removeAA(this)">&times;</span></li>');

                        $('#aa_cat').append('<div class="accordion-item"><h2 class="accordion-header"><button class="accordion-button accordion-category-button accordion-nochild-button collapsed accordion-button-aa">' + selectedVal + '<span class="remove-aa" onClick="removeAA2(this)">&times;</span></button></h2></div>');

                        calcTotal();
                    }
                    inp.value = "";
                    closeAllLists();
                });
                a.appendChild(b);
            }
        }
    });

    inp.addEventListener("keydown", function (e) {
        var x = document.getElementById(this.id + "autocomplete-list");
        if (x) x = x.getElementsByTagName("div");
        if (e.keyCode == 40) {
            currentFocus++;
            addActive(x);
        } else if (e.keyCode == 38) {
            currentFocus--;
            addActive(x);
        } else if (e.keyCode == 13) {
            e.preventDefault();
            if (currentFocus > -1) {
                if (x) x[currentFocus].click();
            }
        }
    });

    function addActive(x) {
        if (!x) return false;
        removeActive(x);
        if (currentFocus >= x.length) currentFocus = 0;
        if (currentFocus < 0) currentFocus = (x.length - 1);
        x[currentFocus].classList.add("autocomplete-active");
    }

    function removeActive(x) {
        for (var i = 0; i < x.length; i++) {
            x[i].classList.remove("autocomplete-active");
        }
    }

    function closeAllLists(elmnt) {
        var x = document.getElementsByClassName("autocomplete-items");
        for (var i = 0; i < x.length; i++) {
            if (elmnt != x[i] && elmnt != inp) {
                x[i].parentNode.removeChild(x[i]);
            }
        }
    }
}

function removeAA(that) {
    let thatText = ($(that).parent().text().trim()).substring(0, ($(that).parent().text().trim()).length - 1);
    let arrIndex = costCalData.additional_activities_arr.indexOf(thatText);
    if (arrIndex !== -1) { costCalData.additional_activities_arr.splice(arrIndex, 1); }
    $(that).parent().remove();
    calcTotal();
}

function removeAA2(that) {
    let thatText = ($(that).parent().text().trim()).substring(0, ($(that).parent().text().trim()).length - 1);
    let arrIndex = costCalData.additional_activities_arr.indexOf(thatText);
    if (arrIndex !== -1) { costCalData.additional_activities_arr.splice(arrIndex, 1); }
    $(that).parent().parent().parent().remove();
    calcTotal();
}

function calcTotal() {
    var year_license = parseInt($('#year_license').val());
    var visas = parseInt($('#visas').val());
    var visa_amt = visas * perVisaCost * year_license;
    var year_total = year_license * oneYearCost;
    var lease_agreement = year_license * leaseAgreementCost;
    var old_total = Math.ceil(year_total + visa_amt + ((costCalData.additional_activities_arr.length) * perAdditionalCost));
    var total = 0;

    if (year_license > 1) {
        total = Math.ceil((year_total * ((100 - savingPercentage) / 100)) + lease_agreement + (visa_amt * ((100 - savingPercentage) / 100)) + ((costCalData.additional_activities_arr.length) * perAdditionalCost));
    } else {
        total = Math.ceil(year_total + lease_agreement + visa_amt + ((costCalData.additional_activities_arr.length) * perAdditionalCost));
    }

    if ($('.form-check-input[name="request"]:checked').val() == "online payment") {
        total -= payOnlineOff;
    }

    $('.old-total').text(numberWithCommas(old_total));
    $('.total').text(numberWithCommas(total));
    if (total < old_total) {
        $('.old-total-outer').removeClass('d-none');
    } else {
        $('.old-total-outer').addClass('d-none');
    }
    $('.form_total input').val(numberWithCommas(total));
}

